import React, { useState } from "react";
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from "react-bootstrap/Col";
const AddData = (props) => {
  const [inputName, setInputName] = useState("");
  const [inputFirstname, setInputFirstname] = useState("");
  const [inputJob, setInputJob] = useState("");
  const [inputEmail, setInputEmail] = useState("");
  const [inputPhone, setInputPhone] = useState("");

  const addDataHandler = (event) => {
    event.preventDefault();
    if (
      inputName.trim().length === 0 ||
      inputFirstname.trim().length === 0 ||
      inputJob.trim().length === 0 ||
      inputEmail.trim().length === 0 ||
      inputPhone.trim().length < 9
    ) {
      return;
    }

    console.log(inputName, inputFirstname, inputJob, inputEmail, inputPhone);
    props.onAddData(
      inputName,
      inputFirstname,
      inputJob,
      inputEmail,
      inputPhone
    );
    setInputName("");
    setInputFirstname("");
    setInputJob("");
    setInputEmail("");
    setInputPhone("");
  };

  const nameChangeHandler = (event) => {
    setInputName(event.target.value);
  };

  const firstnameChangeHandler = (event) => {
    setInputFirstname(event.target.value);
  };

  const jobChangeHandler = (event) => {
    setInputJob(event.target.value);
  };

  const emailChangeHandler = (event) => {
    setInputEmail(event.target.value);
  };

  const phoneChangeHandler = (event) => {
    setInputPhone(event.target.value);
  };

  return (
    <div>
      <Form onSubmit={addDataHandler}>
        <Row className="mb-3">
          <Form.Group as={Col} controlId="formGridName">
            <Form.Label>Imię:</Form.Label>
            <Form.Control
              type="text"
              value={inputName}
              onChange={nameChangeHandler}
            />
          </Form.Group>

          <Form.Group as={Col} controlId="formGridFirstname">
            <Form.Label>Nazwisko:</Form.Label>
            <Form.Control
              type="text"
              value={inputFirstname}
              onChange={fistnameChangeHandler}
            />
          </Form.Group>
        </Row>

        <label>Nazwisko:</label>
        <input
          type="text"
          value={inputFirstname}
          onChange={fistnameChangeHandler}
        />
        <label>Stanowisko:</label>
        <input type="text" value={inputJob} onChange={jobChangeHandler} />
        <label>Email:</label>
        <input type="email" value={inputEmail} onChange={emailChangeHandler} />
        <label>Telefon:</label>
        <input type="tel" value={inputPhone} onChange={phoneChangeHandler} />
        <button type="submit">Potwierdź</button>
      </Form>
    </div>
  );
};
export default AddData;
