import React, { useState } from "react";
import Template from "./components/Template";
import AddData from "./components/AddData";

function App() {
  const [userData, setUserData] = useState([]);

  const addDataHandler = (name, firstname, job, email, phone) => {
    setUserData((prevUserData) => {
      return [
        ...prevUserData,
        {
          name: name,
          firstname: firstname,
          job: job,
          email: email,
          phone: phone,
        },
      ];
    });
  };

  return (
    <div className="join-form__content">
      <div className="form-wrapper"> 
        <img src="https://deligoo.pl/_next/image?url=%2Fimages%2Fshared%2Flogo-with-text.svg&w=256&q=75" style/>
        <h3 className="title">Signature Generator</h3>
        <h5 className="subtitle">for employees of the DeliGoo company</h5>

        <AddData onAddData={addDataHandler} />
        <Template data={userData} />
      </div>
    </div>
  );
}

export default App;
