import React, { useState } from "react";

const AddData = (props) => {
  const [inputName, setInputName] = useState("");
  const [inputFirstname, setInputFirstname] = useState("");
  const [inputJob, setInputJob] = useState("");
  const [inputEmail, setInputEmail] = useState("");
  const [inputPhone, setInputPhone] = useState("");

  const addDataHandler = (event) => {
    event.preventDefault();
    if (
      inputName.trim().length === 0 ||
      inputFirstname.trim().length === 0 ||
      inputJob.trim().length === 0 ||
      inputEmail.trim().length === 0 ||
      inputPhone.trim().length < 9
    ) {
      return;
    }

    console.log(inputName, inputFirstname, inputJob, inputEmail, inputPhone);
    props.onAddData(
      inputName,
      inputFirstname,
      inputJob,
      inputEmail,
      inputPhone
    );
    setInputName("");
    setInputFirstname("");
    setInputJob("");
    setInputEmail("");
    setInputPhone("");

  };

  const nameChangeHandler = (event) => {
    setInputName(event.target.value);
  };

  const firstnameChangeHandler = (event) => {
    setInputFirstname(event.target.value);
  };

  const jobChangeHandler = (event) => {
    setInputJob(event.target.value);
  };

  const emailChangeHandler = (event) => {
    setInputEmail(event.target.value);
  };

  const phoneChangeHandler = (event) => {
    setInputPhone(event.target.value);
  };

  return (
    <div>
      <form onSubmit={addDataHandler}>
        <div className="input-wrapper">
        <input
          className="dg-input"
          placeholder="ImiÄ™"
          type="text"
          value={inputName}
          onChange={nameChangeHandler}
        />
        </div>

        <input
          className="dg-input"
          placeholder="Nazwisko"
          type="text"
          value={inputFirstname}
          onChange={firstnameChangeHandler}
        />
        <div className="input-wrapper">
        <input
          className="dg-input"
          placeholder="Numer telefonu"
          type="tel"
          value={inputPhone}
          onChange={phoneChangeHandler}
        />
        </div>

        <input
          className="dg-input"
          placeholder="Email"
          type="email"
          value={inputEmail}
          onChange={emailChangeHandler}
        />

        <input
          className="dg-input"
          placeholder="Stanowisko"
          type="text"
          value={inputJob}
          onChange={jobChangeHandler}
        />

        <button
          className="btn btn--primary btn--raised btn--animated"
          type="submit"
        >
          PotwierdÅº
        </button>
      </form>
    </div>
  );
};
export default AddData;
