import React, { useState } from "react";
import useInput from "../hooks/use-input";
import ErrorModal from "./ErrorModal";

const AddData = (props) => {

  const {
        value: inputName,
        isValid: inputNameIsValid,
        hasError: inputNameHasHandler,
        inputBlurHandler: inputNameBlurHandler,
        reset: resetInputName,
  } = useInput((value) => value.trim() !== '');

  const {
    value: inputFirstname,
    isValid: inputFirstnameIsValid,
    hasError: inputFirstnameHasHandler,
    inputBlurHandler: inputFirstnameBlurHandler,
    reset: resetInputFirstname,
  } = useInput((value) => value.trim() !== "");

  const {
    value: inputJob,
    isValid: inputJobIsValid,
    hasError: inputJobHasHandler,
    inputBlurHandler: inputJobBlurHandler,
    reset: resetInputJob,
  } = useInput((value) => value.trim() !== "");

  const {
    value: inputEmail,
    isValid: inputEmailIsValid,
    hasError: inputEmailHasHandler,
    inputBlurHandler: inputEmailBlurHandler,
    reset: resetInputEmail,
  } = useInput((value) => value.includes('@'));

  const {
    value: inputPhone,
    isValid: inputPhoneIsValid,
    hasError: inputPhoneHasHandler,
    inputBlurHandler: inputPhoneBlurHandler,
    reset: resetInputPhone,
  } = useInput((value) => value.trim().length() < 9);

  let formIsValid = false;

  if(inputNameIsValid && inputFirstnameIsValid && inputJobIsValid && inputEmailIsValid && inputPhoneIsValid) {
    formIsValid = true;
  }




  const [error, setError] = useState('');



  const addDataHandler = (event) => {
    event.preventDefault();
    if (
      inputName.trim().length === 0 ||
      inputFirstname.trim().length === 0 ||
      inputJob.trim().length === 0 ||
      inputEmail.trim().length === 0 ||
      inputPhone.trim().length < 9
    ) {
      setError({
        title: 'Puste lub błędnie uzupełnione pola!',
        message: 'Proszę poprawnie uzupełnić wszystkie dane!'
      })
      return;
    }

    console.log(inputName, inputFirstname, inputJob, inputEmail, inputPhone);
    props.onAddData(
      inputName,
      inputFirstname,
      inputJob,
      inputEmail,
      inputPhone
    );

    resetInputName();
    resetInput


  };


  const errorHandler = () => {
    setError('');
  }

  return (
    <div>
      {error && ( <ErrorModal title={error.title} message={error.message} onConfirm={errorHandler} /> )}
      <form onSubmit={addDataHandler}>
        <input
          className="dg-input"
          placeholder="Imię"
          type="text"
          value={inputName}
          onBlur={inputNameBlurHandler}
          onChange={nameChangeHandler}
        />

        <input
          className="dg-input"
          placeholder="Nazwisko"
          type="text"
          value={inputFirstname}
          onBlur={inputFirstnameBlurHandler}
          onChange={firstnameChangeHandler}
        />

        <input
          className="dg-input"
          placeholder="Numer telefonu"
          type="tel"
          value={inputPhone}
          onBlur={inputPhoneBlurHandler}
          onChange={phoneChangeHandler}
        />

        <input
          className="dg-input"
          placeholder="Email"
          type="email"
          value={inputEmail}
          onBlur={inputEmailBlurHandler}
          onChange={emailChangeHandler}
        />

        <input
          className="dg-input"
          placeholder="Stanowisko"
          type="text"
          value={inputJob}
          onBlur={inputJobBlurHandler}
          onChange={jobChangeHandler}
        />

        <button
          className="btn btn--primary"
          type="submit"
        >
          Potwierdź
        </button>
      </form>
    </div>
  );
};
export default AddData;
