import React, { useState } from "react";
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from "react-bootstrap/Col";
import Button from 'react-bootstrap/Button';
const AddData = (props) => {
  const [inputName, setInputName] = useState("");
  const [inputFirstname, setInputFirstname] = useState("");
  const [inputJob, setInputJob] = useState("");
  const [inputEmail, setInputEmail] = useState("");
  const [inputPhone, setInputPhone] = useState("");

  const addDataHandler = (event) => {
    event.preventDefault();
    if (
      inputName.trim().length === 0 ||
      inputFirstname.trim().length === 0 ||
      inputJob.trim().length === 0 ||
      inputEmail.trim().length === 0 ||
      inputPhone.trim().length < 9
    ) {
      return;
    }

    console.log(inputName, inputFirstname, inputJob, inputEmail, inputPhone);
    props.onAddData(
      inputName,
      inputFirstname,
      inputJob,
      inputEmail,
      inputPhone
    );
    setInputName("");
    setInputFirstname("");
    setInputJob("");
    setInputEmail("");
    setInputPhone("");
  };

  const nameChangeHandler = (event) => {
    setInputName(event.target.value);
  };

  const firstnameChangeHandler = (event) => {
    setInputFirstname(event.target.value);
  };

  const jobChangeHandler = (event) => {
    setInputJob(event.target.value);
  };

  const emailChangeHandler = (event) => {
    setInputEmail(event.target.value);
  };

  const phoneChangeHandler = (event) => {
    setInputPhone(event.target.value);
  };

  return (
    <div>
      <Form onSubmit={addDataHandler}>
        <Row className="mb-4">
          <Form.Group as={Col} controlId="formGridName">
            <Form.Label>Imię:</Form.Label>
            <Form.Control
              type="text"
              value={inputName}
              onChange={nameChangeHandler}
            />
          </Form.Group>

          <Form.Group as={Col} controlId="formGridFirstname">
            <Form.Label>Nazwisko:</Form.Label>
            <Form.Control
              type="text"
              value={inputFirstname}
              onChange={firstnameChangeHandler}
            />
          </Form.Group>
        </Row>
        <Row className="mb-4">
          <Form.Group as={Col} controlId="formGridJob">
            <Form.Label>Stanowisko:</Form.Label>
            <Form.Control
              type="text"
              value={inputJob}
              onChange={jobChangeHandler}
            />
          </Form.Group>
          <Form.Group as={Col} controlId="formGridEmail">
            <Form.Label>Email:</Form.Label>
            <Form.Control
              type="email"
              value={inputEmail}
              onChange={emailChangeHandler}
            />
          </Form.Group>
        </Row>
        <Row className="align-items-center">
          <Col sm={3} className="my-1">

            <Form.Label>Telefon:</Form.Label>
            <Form.Control
              type="tel"
              value={inputPhone}
              onChange={phoneChangeHandler}
            />
            </Col>
          <Col  className="my-2">
          <Button variant="primary" type="submit" className="mb-4">
            Potwierdź
          </Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
};
export default AddData;
