import classes from "./ErrorModal.module.css";
import Card from "./Card";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { CopyToClipboard } from "react-copy-to-clipboard";

const EndingModal = (props) => {

  const notify = () =>
    toast.success("Kod skopiowano do schowka!", {
      position: "bottom-right",
      autoClose: 3000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
      progress: undefined,
    });
  const onClear = () => {
    setTimeout(() => {
      window.location.reload();
    }, 3500);
  };

  const copyClick = (event) => {
    notify();
    onClear();
  };

    return (
      <div>
        <div className={classes.backdrop} onClick={props.onConfirm} />
        <Card className={classes.modal}>
          <header className={classes.header}>
            <h3>Skopiuj kod html podpisu</h3>
          </header>

        <div className="copy--div">
      {props.data.map((data) => (
        <div key={data.id}>
          <h5>Kod html:</h5>
          <CopyToClipboard
            text={`<html>
    <body style="font-family: Arial; width:350px; text-align:left;">
    <div style="width: 350px;">
        <p style="padding:0px; font-size: 13px;">
        Pozdrawiam
        </p>
        <hr>
        </div>

        <div id="container" style="white-space:nowrap; padding-top: 10px; padding-bottom:0px;">

            <div id="image" style="display:inline;">
                <img src="https://deligoo.pl/_next/image?url=%2Fimages%2Fshared%2Flogo-with-text.svg&w=256&q=75"/>
            </div>

            <div id="texts" style="position: absolute; display:inline-block; white-space:nowrap; padding-left: 20px; margin-top:0; padding-bottom: 0px; font-size: 14px;"> 
                <b>${data.name} ${data.firstname}</b>
                <p style="margin-top: 0px; padding-left: 0px; font-size:13px;">${data.job}
                </p>
            </div>
        </div>
      <div id="contact" style="position: absolute; display:inline-block; white-space:nowrap; padding-left: 73px; margin-top:-15px; padding-bottom: 0px; font-size: 13px;">
          <p style="padding:0px;">+48${data.phone}</p>
          <p style="margin-top:-10px;">${data.email}</p>
      </div>
    </div>
      <div>
          <p style="font-size: 12px; color:#828282;"><b>DeliGoo Sp. z o.o.</b><br> 
            Chodkiewicza 19C/4, 85-065 Bydgoszcz<br>
            Spółka zarejestrowana w Sądzie Rejonowym w Bydgoszczy<br>
            XIII Wydział Gospodarczy Krajowego Rejestru Sądowego,<br> nr KRS 0000683284, NIP 5851478922, Regon 367600052.<br>
            Kapitał zakładowy 15 150,00 PLN, w całości opłacony.</p>
      </div>

    </body>
</html>`}
          >
            <button
              className="btn btn--primary btn--copy"
              onClick={copyClick}
            >
              Kopiuj
            </button>
          </CopyToClipboard>
          <ToastContainer />
        </div>
      ))}
      </div>      

        </Card>
      </div>
    );
};
export default EndingModal;
