import React, { useState } from 'react';
import Template from './components/Template'
import AddData from './components/AddData';
import Container from "react-bootstrap/Container";


function App() {
  const [userData, setUserData] = useState([]);

  const addDataHandler = (name, firstname, job, email, phone) =>{
    setUserData((prevUserData) => {
      return [
        ...prevUserData,
        {
          name: name,
          firstname: firstname,
          job: job,
          email: email,
          phone: phone
        }
      ]
    })
  }

  return (
    <Container fluid="md" className='p-5' style={{ backgroundColor: "grey", borderColor: 'black', border: '2px solid', borderRadius: '10px', } }>
      <div>
        <AddData onAddData={addDataHandler} />
        <Template data={userData} />
      </div>
    </Container>
  );
}

export default App;
