import React, { useState } from "react";
import useInput from "../hooks/use-input";
import ErrorModal from "./ErrorModal";
import classes from "../App.module.css";
const AddData = (props) => {
  const [error, setError] = useState("");

  const {
    value: inputName,
    isValid: inputNameIsValid,
    hasError: inputNameHasError,
    valueChangeHandler: nameChangeHandler,
    inputBlurHandler: inputNameBlurHandler,
    reset: resetInputName,
  } = useInput((value) => value.trim() !== "");

  const {
    value: inputFirstname,
    isValid: inputFirstnameIsValid,
    hasError: inputFirstnameHasError,
    valueChangeHandler: firstnameChangeHandler,
    inputBlurHandler: inputFirstnameBlurHandler,
    reset: resetInputFirstname,
  } = useInput((value) => value.trim() !== "");

  const {
    value: inputJob,
    isValid: inputJobIsValid,
    hasError: inputJobHasError,
    valueChangeHandler: jobChangeHandler,
    inputBlurHandler: inputJobBlurHandler,
    reset: resetInputJob,
  } = useInput((value) => value.trim() !== "");

  const {
    value: inputEmail,
    isValid: inputEmailIsValid,
    hasError: inputEmailHasError,
    valueChangeHandler: emailChangeHandler,
    inputBlurHandler: inputEmailBlurHandler,
    reset: resetInputEmail,
  } = useInput((value) => value.includes("@"));

  const {
    value: inputPhone,
    isValid: inputPhoneIsValid,
    hasError: inputPhoneHasError,
    valueChangeHandler: phoneChangeHandler,
    inputBlurHandler: inputPhoneBlurHandler,
    reset: resetInputPhone,
  } = useInput((value) => value.trim() !== "" && value.length > 8);

  const {
    value: inputImg,
    valueChangeHandler: imgChangeHandler,
    inputBlurHandler: inputImgBlurHandler,
    reset: resetInputImg,
  } = useInput((value) => value);

  let formIsValid = false;

  if (
    inputNameIsValid &&
    inputFirstnameIsValid &&
    inputJobIsValid &&
    inputEmailIsValid &&
    inputPhoneIsValid
  ) {
    formIsValid = true;
  }

  const addDataHandler = (event) => {
    event.preventDefault();
    console.log("confirm");

    if (!formIsValid) {
      setError({
        title: "Brakujące dane w formularzu",
        message: "Proszę uzupełnić wszystkie pola.",
      });
      return;
    }

    console.log(inputName, inputFirstname, inputJob, inputEmail, inputPhone);
    props.onAddData(
      inputName,
      inputFirstname,
      inputJob,
      inputEmail,
      inputPhone,
      inputImg
    );

    resetInputName();
    resetInputFirstname();
    resetInputJob();
    resetInputEmail();
    resetInputPhone();
    resetInputImg();
  };

  const errorHandler = () => {
    setError("");
  };

  return (
    <div>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={errorHandler}
        />
      )}
      <form onSubmit={addDataHandler}>
        <div>
          <input
            className="dg-input"
            placeholder="*Imię"
            type="text"
            onBlur={inputNameBlurHandler}
            onChange={nameChangeHandler}
            value={inputName}
          />
          {inputNameHasError && (
            <p className={classes.errorp}>Wprowadź imię.</p>
          )}
        </div>
        <div>
          <input
            className="dg-input"
            placeholder="*Nazwisko"
            type="text"
            onBlur={inputFirstnameBlurHandler}
            onChange={firstnameChangeHandler}
            value={inputFirstname}
          />
          {inputFirstnameHasError && (
            <p className={classes.errorp}>Wprowadź nazwisko.</p>
          )}
        </div>
        <div>
          <input
            className="dg-input"
            placeholder="*Numer telefonu"
            type="tel"
            onBlur={inputPhoneBlurHandler}
            onChange={phoneChangeHandler}
            value={inputPhone}
          />
          {inputPhoneHasError && (
            <p className={classes.errorp}>Wprowadź numer telefonu.</p>
          )}
        </div>
        <div>
          <input
            className="dg-input"
            placeholder="*Email"
            type="email"
            onBlur={inputEmailBlurHandler}
            onChange={emailChangeHandler}
            value={inputEmail}
          />
          {inputEmailHasError && (
            <p className={classes.errorp}>Wprowadź adres email.</p>
          )}
        </div>
        <div>
          <input
            className="dg-input"
            placeholder="*Stanowisko"
            type="text"
            onBlur={inputJobBlurHandler}
            onChange={jobChangeHandler}
            value={inputJob}
          />
          {inputJobHasError && (
            <p className={classes.errorp}>Wprowadź zajmowane stanowisko.</p>
          )}
        </div>

        <div className="dg-input" style={{ cursor: "pointer" }} tabIndex="0">
          <input
            accept="application/pdf,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
            type="file"
            autocomplete="off"
            tabIndex="-1"
            style={{ opacity: 0 }}
          />
          <p style={{marginTop: '-20px'}}>Załącz zdjęcie .jpg</p>
        </div>
        <div>
          <button className="btn btn--primary float--btn" type="submit">
            Potwierdź
          </button>
        </div>
      </form>
    </div>
  );
};
export default AddData;
